
public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 110;
		byte b = (byte)a; //casting from byte to int
		
		
		float f = 5.6f;
		int n =(int)f;   //casting from float to int
		
		int c = 1000;
		double d = (int)c;   ///casting from int to double
		
		
		System.out.println(d);
	
	}

}
